import matplotlib.pyplot as plt
from shapely.geometry import Polygon
from matplotlib.patches import Rectangle
from Kinematics import *


def visualize_robot_arms(points, obstacles, multiple_arms=False, intersection=False):
    
     # Create a plot
    fig, ax = plt.subplots()

    # want to be able to visualize the obstacles no matter how many we have
    for obstacle in obstacles:
        # Create the polygon
        polygon = Polygon(obstacle)

        # Extract the x and y coordinates from the polygon
        x1, y1 = polygon.exterior.xy

        # Add the polygon to the plot
        ax.fill(x1, y1)
    
    if multiple_arms:
        for point in points:
                # Unpack the points into two lists, xs and ys
            xs, ys = zip(*point)

            # Plot the points and connect them with lines
            ax.plot(xs, ys, marker='o')
    
    else:

        # Unpack the points into two lists, xs and ys
        xs, ys = zip(*points)

        # Plot the points and connect them with lines
        ax.plot(xs, ys, marker='o')

    # If intersection is True, add a red shaded overlay
    if intersection:
        ax.add_patch(Rectangle((ax.get_xlim()[0], ax.get_ylim()[0]),  # Bottom left corner
                               ax.get_xlim()[1]-ax.get_xlim()[0],     # Width
                               ax.get_ylim()[1]-ax.get_ylim()[0],     # Height
                               color='red',                           # Color
                               alpha=0.4))   


    # Optionally, you can add labels and a grid for better readability
    plt.xlabel('X Coordinate')
    plt.ylabel('Y Coordinate')
    plt.title('Robot Arm Configuration')
    plt.grid(True)

    # Display the plot
    plt.show()

